select board_no as no, title, content, created_date as createdDate, view_count from ml_board where board_no=#{no}
insert into ml_board(title,content) values(#{title},#{content})
update ml_board set title=#{title}, content=#{content}, where board_no=#{no}
select count(*) from ml_board
